package basicJava;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Primitive datatypes >> int, double, char, boolean

		// int
		int i = 5;
		i = 20; // i changed to 20

		int j = 6;
		int l = 70;
		System.out.println(l);
		int k = -2;
		System.out.println(k);
		int p = 0;
		System.out.println(i + j);

		// ignore yellow signs because its saying that you have created variables but
		// not using them.

		// double
		double t = 11.4;
		double e2 = 100; // 100.0
		System.out.println(e2);
		double r = 44.77;
		System.out.println(r);

		// char >> only single digit and should be written in single quotes
		char m = 'a';
		char n = 'A';
		System.out.println(n);
		char u3 = '1';
		char s = '$';
		System.out.println(s);

		// boolean
		boolean t1 = true;
		boolean b2 = false;
		System.out.println(b2);

		// String is a Class, not a datatype
		// should be written in double quotes

		String c1 = "harman";
		System.out.println(c1);
		String c2 = "1000";
		String c3 = "11.22";
		System.out.println(c3);
		String c4 = "this is java code";
		System.out.println(c4);

	}

}
